import createDOMPurify from 'dompurify';

const DOMPurify = createDOMPurify(window);

// Mermaid (notably class/sequence renderers in some versions) may import the `dompurify`
// factory function and call instance methods like `addHook()` on it. Decorate the factory
// export with the real instance methods so both import styles work in our bundled webview.
Object.assign(createDOMPurify, DOMPurify);

export default DOMPurify;
